  shadow: 'rgba(107, 119, 141, 0.2)',
  inputBackground: '#EDF2F7',
  button: '#8DB5EE',
  buttonText: '#FFFFFF',
  mediatorBubble: '#EBF2FB',
  userBubble: '#8DB5EE', 
  userBubbleText: '#FFFFFF',
  breatherGradientStart: '#80D8C3',
  breatherGradientEnd: '#A0E5D3',
};

const ICONS = {
    wechat: "M20 2H4C2.9 2 2 2.9 2 4V22L6 18H20C21.1 18 22 17.1 22 16V4C22 2.9 21.1 2 20 2ZM20 16H6L4 18V4H20V16Z",
    dumbbell: "M21.78 9.53L18 5.75V3H16V5.17L12.83 2H11.17L8 5.17V3H6V5.75L2.22 9.53C1.53 10.22 1.44 11.35 2 12.16C2.56 12.97 3.69 13.06 4.47 12.47L6 11.22V19H8V11.22L11.17 8H12.83L16 11.22V19H18V11.22L19.53 12.47C20.31 13.06 21.44 12.97 22 12.16C22.56 11.35 22.47 10.22 21.78 9.53Z",
    meditation: "M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zM12 20c-4.41 0-8-3.59-8-8s3.59-8 8-8 8 3.59 8 8-3.59 8-8 8zm-3.5-3.5l2.44-2.44L12 15.17l1.06-1.06-2.44-2.44L12 10.59l3.5 3.5-4.56 4.56C12.96 18.23 12 17.26 12 16v-1.5l-3.5-3.5zM15.5 10.5c.83 0 1.5-.67 1.5-1.5s-.67-1.5-1.5-1.5-1.5.67-1.5 1.5.67 1.5 1.5 1.5z",
    avatar: "M12 12c2.21 0 4-1.79 4-4s-1.79-4-4-4-4 1.79-4 4 1.79 4 4 4zm0 2c-2.67 0-8 1.34-8 4v2h16v-2c0-2.66-5.33-4-8-4z",
    gestures: "M12 21.35l-1.45-1.32C5.4 15.36 2 12.28 2 8.5 2 5.42 4.42 3 7.5 3c1.74 0 3.41.81 4.5 2.09C13.09 3.81 14.76 3 16.5 3 19.58 3 22 5.42 22 8.5c0 3.78-3.4 6.86-8.55 11.54L12 21.35z",
    communication: "M20 2H4c-1.1 0-2 .9-2 2v18l4-4h14c1.1 0 2-.9 2-2V4c0-1.1-.9-2-2-2zm-2 12H6v-2h12v2zm0-3H6V9h12v2zm0-3H6V6h12v2z",
    love_languages: "M12.1 18.55l-.1.1-.1-.1C7.14 14.24 4 11.39 4 8.5 4 6.5 5.5 5 7.5 5c1.54 0 3.04.99 3.57 2.36h1.87C13.46 5.99 14.96 5 16.5 5 18.5 5 20 6.5 20 8.5c0 2.89-3.14 5.74-7.9 10.05z"
};

// --- Shared Components ---
const Icon = ({ path, color, size = 32 }) => (
  <svg width={size} height={size} viewBox="0 0 24 24" fill={color} xmlns="http://www.w3.org/2000/svg">
    <path fillRule="evenodd" clipRule="evenodd" d={path} />
  </svg>
);

const ChevronRightIcon = () => (
    <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
        <path d="M9 18L15 12L9 6" stroke={COLORS.textSecondary} strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
    </svg>
);

const BackArrowIcon = () => (
    <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
      <path d="M15 18L9 12L15 6" stroke={COLORS.text} strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
    </svg>
);

const SendIcon = () => (
    <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
        <path d="M22 2L11 13" stroke={COLORS.buttonText} strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
        <path d="M22 2L15 22L11 13L2 9L22 2Z" stroke={COLORS.buttonText} strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
    </svg>
);

const PlayIcon = () => (
    <svg width="48" height="48" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
        <path d="M5 3L19 12L5 21V3Z" stroke={COLORS.buttonText} strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
    </svg>
);

const FeatureCard = ({ styles, iconPath, title, description, color, onPress }) => (
  <div style={styles.cardContainer} onClick={onPress} role="button" tabIndex="0">
    <div style={{...styles.cardIconContainer, backgroundColor: `${color}20`}}>
      <Icon path={iconPath} color={color} />
    </div>
    <div style={styles.cardTextContainer}>
      <p style={styles.cardTitle}>{title}</p>
      <p style={styles.cardDescription}>{description}</p>
    </div>
    <div style={styles.cardArrow}>
      <ChevronRightIcon />
    </div>
  </div>
);

// --- Feature Screen Components ---

const AuthScreen = ({ setView, styles }) => (
  <div style={styles.authContainer}>
    <h1 style={styles.authTitle}>Connect</h1>
    <p style={styles.authSubtitle}>Sign in to continue</p>
    <input style={styles.input} placeholder="Email" type="email" />
    <input style={styles.input} placeholder="Password" type="password" />
    <button style={styles.button} onClick={() => setView('home')}>
      Sign In
    </button>
    <p style={styles.authFooter}>Don't have an account? <a href="#" style={{color: COLORS.accent1}}>Sign Up</a></p>
  </div>
);

const HomeScreen = ({ setView, styles }) => (
  <div style={styles.scrollViewContainer}>
    <div style={styles.header}>
      <h1 style={styles.headerTitle}>Welcome</h1>
      <p style={styles.headerSubtitle}>How can we help you connect today?</p>
    </div>
    <div style={styles.featuresContainer}>
      <FeatureCard
        styles={styles}
        iconPath={ICONS.wechat}
        title="Resolve a Conflict"
        description="A step-by-step guide to talk through a specific issue productively."
        color={COLORS.accent1}
        onPress={() => setView('mediator_chat')}
      />
      <FeatureCard
        styles={styles}
        iconPath={ICONS.dumbbell}
        title="Build Healthy Habits"
        description="Daily exercises and guides to strengthen your communication skills."
        color={COLORS.accent2}
        onPress={() => setView('healthy_habits')}
      />
      <FeatureCard
        styles={styles}
        iconPath={ICONS.meditation}
        title="Take a Breather"
        description="A tool to pause a heated argument and cool down before you reconnect."
        color={COLORS.accent3}
        onPress={() => setView('cooldown')}
      />
    </div>
  </div>
);

const MediatorChatScreen = ({ setView, styles }) => {
    const [sessionStarted, setSessionStarted] = useState(false);
    const [messages, setMessages] = useState([
        { 
            sender: 'ai', 
            text: "Welcome. This is a private and secure space to communicate. When you're ready, please press 'Start Session' below." 
        }
    ]);
    const [inputValue, setInputValue] = useState('');
    const messageAreaRef = useRef(null);

    useEffect(() => {
        if (messageAreaRef.current) {
            messageAreaRef.current.scrollTop = messageAreaRef.current.scrollHeight;
        }
    }, [messages]);

    const handleStartSession = () => {
        setMessages(prev => [...prev, {
            sender: 'ai',
            text: "Great. To begin, please take a moment to describe the situation from your perspective. What happened that led to this conflict?"
        }]);
        setSessionStarted(true);
    };

    const handleSendMessage = () => {
        if (inputValue.trim() === '') return;

        const userMessage = { sender: 'user', text: inputValue };
        setMessages(prev => [...prev, userMessage]);
        setInputValue('');

        setTimeout(() => {
            const aiResponse = { sender: 'ai', text: "Thank you for sharing. Can you tell me more about how that made you feel?" };
            setMessages(prev => [...prev, aiResponse]);
        }, 1200);
    };

    return (
        <div style={styles.chatContainer}>
            <div style={styles.pageHeader}>
                <button onClick={() => setView('home')} style={styles.backButton}>
                    <BackArrowIcon />
                </button>
                <div style={styles.chatHeaderInfo}>
                    <div style={styles.avatar}>
                        <Icon path={ICONS.avatar} color={COLORS.accent1} size={24}/>
                    </div>
                    <p style={styles.chatHeaderTitle}>AI Mediator</p>
                </div>
            </div>
            <div style={styles.messageArea} ref={messageAreaRef}>
                {messages.map((msg, index) => (
                    <div key={index} style={msg.sender === 'ai' ? styles.mediatorMessageWrapper : styles.userMessageWrapper}>
                         <div style={msg.sender === 'ai' ? styles.mediatorMessageBubble : styles.userMessageBubble}>
                            <p style={msg.sender === 'ai' ? styles.messageText : styles.userMessageText}>{msg.text}</p>
                        </div>
                    </div>
                ))}
            </div>
            <div style={styles.chatFooter}>
                 {!sessionStarted ? (
                     <button style={{...styles.button, width: '100%'}} onClick={handleStartSession}>
                        Start Session
                    </button>
                 ) : (
                     <div style={styles.chatInputContainer}>
                         <input
                            style={styles.chatInput}
                            placeholder="Type your message..."
                            value={inputValue}
                            onChange={(e) => setInputValue(e.target.value)}
                            onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
                         />
                         <button style={styles.sendButton} onClick={handleSendMessage}>
                            <SendIcon />
                         </button>
                     </div>
                 )}
            </div>
        </div>
    );
};

const CooldownScreen = ({ setView, styles }) => {
    const [isPlaying, setIsPlaying] = useState(false);
    const [guideText, setGuideText] = useState("");
    const svgRef = useRef(null);
    const animationTimeoutRef = useRef(null);
    const cycleCountRef = useRef(0);

    const handleGoBack = () => {
        setIsPlaying(false);
        setView('home');
    }

    const handlePlay = () => {
        cycleCountRef.current = 0;
        setIsPlaying(true);
    };

    useEffect(() => {
        if (!isPlaying) {
            return;
        }

        const svg = d3.select(svgRef.current);
        svg.select("circle").remove();
        const circle = svg.append("circle")
            .attr("cx", 100)
            .attr("cy", 100)
            .attr("r", 50) 
            .attr("fill", "url(#grad1)");

        let cancelled = false;

        const breathCycle = () => {
            if (cancelled || cycleCountRef.current >= 10) {
                setGuideText("Session Complete");
                setIsPlaying(false);
                return;
            }
            cycleCountRef.current++;

            setGuideText("Inhale");
            circle.transition()
                .duration(15000)
                .ease(d3.easeCircleOut)
                .attr("r", 100)
                .on("end", () => {
                    if (cancelled) return;

                    setGuideText("Hold");
                    animationTimeoutRef.current = setTimeout(() => {
                        if (cancelled) return;

                        setGuideText("Exhale");
                        circle.transition()
                            .duration(15000)
                            .ease(d3.easeCircleOut)
                            .attr("r", 50)
                            .on("end", () => {
                                if (cancelled) return;

                                setGuideText("Hold");
                                animationTimeoutRef.current = setTimeout(breathCycle, 5000);
                            });
                    }, 5000);
                });
        };

        breathCycle();

        return () => {
            cancelled = true;
            if (animationTimeoutRef.current) {
                clearTimeout(animationTimeoutRef.current);
            }
            if (svgRef.current) {
                 const circleEl = d3.select(svgRef.current).select("circle");
                 if(circleEl && circleEl.interrupt) circleEl.interrupt();
            }
        };
    }, [isPlaying]);

    return (
        <div style={styles.cooldownContainer}>
            <div style={styles.pageHeader}>
                <button onClick={handleGoBack} style={styles.backButton}>
                    <BackArrowIcon />
                </button>
            </div>
            <div style={styles.cooldownContent}>
                {!isPlaying ? (
                    <div style={styles.cooldownIntro}>
                        <h2 style={styles.cooldownTitle}>Time for a Breather</h2>
                        <p style={styles.cooldownSubtitle}>
                           {guideText === "Session Complete" ? guideText : "Follow the guide to calm your mind and body."}
                        </p>
                        <button style={styles.playButton} onClick={handlePlay}>
                            <PlayIcon />
                        </button>
                    </div>
                ) : (
                    <div style={styles.animationContainer}>
                        <svg ref={svgRef} width="300" height="300" viewBox="0 0 200 200">
                             <defs>
                                <radialGradient id="grad1" cx="50%" cy="50%" r="50%" fx="50%" fy="50%">
                                <stop offset="0%" style={{stopColor: COLORS.breatherGradientEnd, stopOpacity:1}} />
                                <stop offset="100%" style={{stopColor: COLORS.breatherGradientStart, stopOpacity:1}} />
                                </radialGradient>
                            </defs>
                        </svg>
                        <p style={styles.guideText}>{guideText}</p>
                    </div>
                )}
            </div>
        </div>
    );
};

const HealthyHabitsScreen = ({ setView, styles, onSelectModule }) => {
    const modules = [
        {
            id: 'gestures',
            title: 'Small Gestures, Big Impact',
            description: "Discover small, daily actions that nurture connection and appreciation in your relationship.",
            iconPath: ICONS.gestures,
            color: COLORS.accent3
        },
        {
            id: 'communication',
            title: 'Essential Communication Tips',
            description: "Learn foundational techniques for clearer, kinder, and more effective day-to-day conversations.",
            iconPath: ICONS.communication,
            color: COLORS.accent1
        },
        {
            id: 'love_languages',
            title: "Speak Your Partner's Love Language",
            description: "Understand how your partner gives and receives love, and learn to express affection in ways that resonate most.",
            iconPath: ICONS.love_languages,
            color: COLORS.accent2
        }
    ];

    return (
        <div style={styles.learningContainer}>
            <div style={styles.pageHeader}>
                <button onClick={() => setView('home')} style={styles.backButton}>
                    <BackArrowIcon />
                </button>
                <h1 style={styles.learningTitle}>Build Healthy Habits</h1>
            </div>
             <div style={{paddingTop: '80px', paddingLeft: '20px', paddingRight: '20px'}}>
                {modules.map((module) => (
                    <FeatureCard
                        key={module.id}
                        styles={styles}
                        iconPath={module.iconPath}
                        title={module.title}
                        description={module.description}
                        color={module.color}
                        onPress={() => onSelectModule(module)}
                    />
                ))}
            </div>
        </div>
    );
};

const Calendar = ({ styles }) => {
    const today = new Date();
    const currentDay = today.getDate();
    const currentMonth = today.toLocaleString('default', { month: 'long' });
    const days = ['S', 'M', 'T', 'W', 'T', 'F', 'S'];
    const startOfMonth = new Date(today.getFullYear(), today.getMonth(), 1);
    const firstDayOfWeek = startOfMonth.getDay(); 

    let dates = [];
    for (let i = 0; i < firstDayOfWeek; i++) {
        dates.push(null);
    }
    const daysInMonth = new Date(today.getFullYear(), today.getMonth() + 1, 0).getDate();
    for (let i = 1; i <= daysInMonth; i++) {
        dates.push(i);
    }

    return (
        <div style={styles.calendarContainer}>
            <div style={styles.calendarHeader}>
                <p style={styles.calendarMonth}>{currentMonth} {today.getFullYear()}</p>
            </div>
            <div style={styles.calendarWeek}>
                {days.map((day, index) => <div key={`${day}-${index}`} style={{...styles.calendarDay, ...styles.dayName}}>{day}</div>)}
            </div>
            <div style={{display: 'grid', gridTemplateColumns: 'repeat(7, 1fr)', rowGap: '4px', placeItems: 'center'}}>
                 {dates.map((date, index) => (
                    <div key={index} style={{
                        ...styles.calendarDay, 
                        ...(date === currentDay ? styles.currentDay : {})
                    }}>
                        {date}
                    </div>
                ))}
            </div>
        </div>
    )
}

const LearningModuleScreen = ({ module, setView, styles }) => {
    return (
        <div style={styles.learningContainer}>
            <div style={styles.pageHeader}>
                <button onClick={() => setView('healthy_habits')} style={styles.backButton}>
                    <BackArrowIcon />
                </button>
            </div>
             <div style={{paddingTop: '80px', paddingLeft: '20px', paddingRight: '20px'}}>
                <Calendar styles={styles} />
                <div style={styles.moduleContent}>
                    <h2 style={styles.moduleTitle}>{module.title}</h2>
                    <p style={styles.moduleText}>{module.description}</p>
                    <button style={{...styles.button, marginTop: '20px', backgroundColor: module.color || COLORS.button}}>
                        Commit to this Habit Today
                    </button>
                </div>
            </div>
        </div>
    );
};


// --- Main App Component ---
export default function App() {
  const [currentView, setCurrentView] = useState({ view: 'auth', props: {} });

  const navigate = (view, props = {}) => {
    setCurrentView({ view, props });
  };

  const selectModule = (module) => {
    navigate('learning_module', { module });
  };

  const renderView = () => {
    switch (currentView.view) {
      case 'home':
        return <HomeScreen setView={navigate} styles={styles} />;
      case 'mediator_chat':
        return <MediatorChatScreen setView={navigate} styles={styles} />;
      case 'cooldown':
        return <CooldownScreen setView={navigate} styles={styles} />;
      case 'healthy_habits':
        return <HealthyHabitsScreen setView={navigate} styles={styles} onSelectModule={selectModule} />;
      case 'learning_module':
        return <LearningModuleScreen module={currentView.props.module} setView={navigate} styles={styles} />;
      case 'auth':
      default:
        return <AuthScreen setView={navigate} styles={styles} />;
    }
  };

  return (
    <div style={styles.safeArea}>
      {renderView()}
    </div>
  );
}

// --- Stylesheet Object ---
const styles = {
  safeArea: {
    backgroundColor: '#F7F9FC',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif',
    minHeight: '100vh',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
  // Home & General
  scrollViewContainer: { width: '100%', maxWidth: '420px', margin: '0 auto', paddingBottom: 40, alignSelf: 'flex-start' },
  header: { padding: '40px 24px 20px 24px' },
  headerTitle: { fontSize: 32, fontWeight: 'bold', color: '#333D4B', marginBottom: 8, margin: 0 },
  headerSubtitle: { fontSize: 18, color: '#6B778D', margin: 0 },
  featuresContainer: { padding: '0 20px' },
  cardContainer: {
    backgroundColor: '#FFFFFF', borderRadius: 16, padding: 20, display: 'flex',
    flexDirection: 'row', alignItems: 'center', marginBottom: 16,
    boxShadow: '0 4px 12px rgba(107, 119, 141, 0.2)', cursor: 'pointer',
  },
